import React, { useState } from "react";
import { Link } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';

import NewCard from "./NewCard"

function CardForm(prop) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Button variant="sm btn-primary" onClick={handleShow}>
        + ADD
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Adding A Card ON {prop.list.name} List</Modal.Title>
        </Modal.Header>
        <Modal.Body>
             Woohoo, you're reading this text in a modal!
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

class List extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      boards: []
    };
  }

  componentDidMount() {
    const url = "/api/v1/list/index";
    fetch(url)
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error("Network response was not ok.");
      })
      .then(response => this.setState({ boards: response }))
      .catch(() => this.props.history.push("/"));
  }

  render() {
    const { boards } = this.state;
    const all_boards = boards.map((board, index) => (
      <div key={index} className="vw-100 vh-100 primary-color d-flex align-items-center justify-content-center">
        <div className="jumbotron jumbotron-fluid bg-transparent">
          <div className="container secondary-color">
            <div className="row">
              <h4 className="display-6">{board.name}</h4>
            </div>
            <div className="leads row">

              {
                board.lists.map(list => {
                  return (
                    <div className="card" style={{ backgroundColor: '#E4EBEF' }}>
                      <div className="card-body">
                        <h6 className="card-title">
                           <div className="row">
                              <div className="col-sm-9"><strong>{list.name}</strong></div>
                              <div className="col-sm-3">
                                <CardForm list={list}/>
                              </div>
                           </div>
                        </h6>
                        <p className="card-text">
                          {
                            list.cards.map(card => {
                              return (
                                <div className="row card-text-row">
                                  {card.name}
                                </div>
                               )
                            })
                          }
                        </p>
                      </div>
                    </div>
                  )
                })
              }
            </div>
          </div>
        </div>
      </div>
    ));


    return (
      <>
      {all_boards}
      </>
    );
  }

}
export default List;
