import React from "react";
import { Link } from "react-router-dom";

class List extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      boards: []
    };
  }

  componentDidMount() {
    const url = "/api/v1/list/index";
    fetch(url)
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error("Network response was not ok.");
      })
      .then(response => this.setState({ boards: response }))
      .catch(() => this.props.history.push("/"));
  }

  render() {
    const { boards } = this.state;
    const all_boards = boards.map((board, index) => (
      <div key={index} className="vw-100 vh-100 primary-color d-flex align-items-center justify-content-center">
        <div className="jumbotron jumbotron-fluid bg-transparent">
          <div className="container secondary-color">
            <div className="row">
              <h4 className="display-6">{board.name}</h4>
            </div>
            <div className="leads row">
              <div className="card" style={{ backgroundColor: '#E4EBEF' }}>
                <div className="card-body">
                  <h4 className="card-title">
                     <div className="row">
                        <div className="col-sm-9">Card title</div><div className="col-sm-3"><a href="#" className="btn btn-sm btn-primary">+ ADD</a></div>
                     </div>
                  </h4>
                  <p className="card-text">
                    <div className="row card-text-row">
                      Some quick example text to build on the card title and make up the bulk of the card's content.
                    </div>
                    <div className="row card-text-row">
                      Some quick example text to build
                    </div>
                  </p>
                </div>
              </div>

            </div>
          </div>
        </div>
      </div>


    ));

    return (
      <>
      {all_lists}
      </>
    );
  }

}
export default List;
