import React, { Component }               from 'react'
import { Link }                           from 'react-router-dom'

class List extends Component {

  state = {
    posts: []
  }

  componentDidMount() {
    fetch('/list')
      .then(boards => boards.json())
      .then(boards => {
        this.setState({
          boards: boards
        })
      })
  }

  renderPosts = () => {
    return (<div className="panel panel-default">
              <div >
                <table className="table table-striped table-bordered  table-hover table-condensed table-responsive ">
                  <thead>
                    <tr>
                      <th>Name</th>
                    </tr>
                  </thead>
                  <tbody>
                    {
                      this.state.boards.map(board => {
                        return (
                          <tr >
                            <td>{board.name}</td>
                          </tr>
                        )
                      })
                    }
                  </tbody>
                </table>
              </div>
            </div>
          )
  }

  render() {
    return (
      <div>
        List Component
        {this.renderPosts()}
        <Link to="/posts/new">Add a New Post</Link>
      </div>
    )
  }
}

export default List
